import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { searchItems } from './services/searchService';
import { SearchBar } from './components/SearchBar';
import { SearchResults } from './components/SearchResults';
import { FacetFilters } from './components/FacetFilters';
import { DetailPanel } from './components/DetailPanel';
import { Pagination } from './components/Pagination';
import ApiTest from './components/ApiTest';
import SearchHighlightTest from './components/SearchHighlightTest';
import SemanticSearchTest from './components/SemanticSearchTest';

function App() {
  // State
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [facets, setFacets] = useState({});
  const [selectedFacets, setSelectedFacets] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showDetailPanel, setShowDetailPanel] = useState(false);
  const [totalCount, setTotalCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('relevance');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showDownloadOptions, setShowDownloadOptions] = useState(false);
  const [searchPerformed, setSearchPerformed] = useState(false);
  const [spellingSuggestions, setSpellingSuggestions] = useState(null);
  const [queryType, setQueryType] = useState('hybrid'); // Default to hybrid search
  const pageSize = 10;

  // Load default results when the page loads
  useEffect(() => {
    handleSearch();
  }, []);

  useEffect(() => {
    // Search when facets or sort options change
    if (searchPerformed) {
      handleSearch();
    }
  }, [selectedFacets, sortBy]);

  // Add event listener for ESC key to close sidebar on mobile
  useEffect(() => {
    const handleEscKey = (event) => {
      if (event.key === 'Escape') {
        setSidebarOpen(false);
        if (showDetailPanel) {
          setShowDetailPanel(false);
        }
      }
    };

    window.addEventListener('keydown', handleEscKey);
    return () => {
      window.removeEventListener('keydown', handleEscKey);
    };
  }, [showDetailPanel]);

  // Handle search
  const handleSearch = async (page = currentPage) => {
    if (!query.trim()) {
      setLoading(true);
      setError(null);
      setSearchPerformed(true);
      setSpellingSuggestions(null); // Clear any previous spelling suggestions

      try {
        console.log('Performing search with empty query');
        const response = await searchItems('', selectedFacets, page, sortBy);

        setResults(response.results);
        setFacets(response.facets);
        setTotalCount(response.count);
        setCurrentPage(page);

        console.log('Search results:', response.results.length);
        console.log('Total count:', response.count);
      } catch (err) {
        console.error('Search error:', err);
        setError(`An error occurred while searching: ${err.message || 'Please try again'}`);
        setResults([]);
        setFacets({});
        setTotalCount(0);
      } finally {
        setLoading(false);
      }
    } else {
      setLoading(true);
      setError(null);
      setSearchPerformed(true);

      try {
        console.log('Performing search with query:', query);
        console.log('Using query type:', queryType);

        // Use the hybrid search approach by default
        const response = await searchItems(query, selectedFacets, page, sortBy, queryType);

        setResults(response.results);
        setFacets(response.facets);
        setTotalCount(response.count);
        setCurrentPage(page);

        // Handle spelling suggestions if available
        if (response.spellingSuggestions) {
          setSpellingSuggestions(response.spellingSuggestions);
        } else {
          setSpellingSuggestions(null);
        }

        console.log('Search results:', response.results.length);
        console.log('Total count:', response.count);
      } catch (err) {
        console.error('Search error:', err);
        setError(`An error occurred while searching: ${err.message || 'Please try again'}`);
        setResults([]);
        setFacets({});
        setTotalCount(0);
        setSpellingSuggestions(null);
      } finally {
        setLoading(false);
      }
    }
  };

  // Handle query change
  const handleQueryChange = (newQuery) => {
    setQuery(newQuery);
  };

  // Handle query submit
  const handleQuerySubmit = () => {
    setCurrentPage(1);
    handleSearch(1);
  };

  // Format facet name for display
  const formatFacetName = (name) => {
    if (!name) return '';

    // Specific display names for our allowed facets
    if (name === 'CategoryDescription') return 'Category';
    if (name === 'GroupDescription') return 'Group';

    // Convert camelCase or snake_case to Title Case with spaces
    return name
      .replace(/([A-Z])/g, ' $1') // Insert space before capital letters
      .replace(/_/g, ' ') // Replace underscores with spaces
      .replace(/^\w/, c => c.toUpperCase()) // Capitalize first letter
      .trim();
  };

  // Handle facet selection
  const handleFacetChange = (facetName, facetValue, isChecked) => {
    setSelectedFacets(prevFacets => {
      const updatedFacets = { ...prevFacets };

      if (!updatedFacets[facetName]) {
        updatedFacets[facetName] = [];
      }

      if (isChecked) {
        // Add the value if it's not already in the array
        if (!updatedFacets[facetName].includes(facetValue)) {
          updatedFacets[facetName] = [...updatedFacets[facetName], facetValue];
        }
      } else {
        // Remove the value from the array
        updatedFacets[facetName] = updatedFacets[facetName].filter(value => value !== facetValue);

        // Remove the facet key if the array is empty
        if (updatedFacets[facetName].length === 0) {
          delete updatedFacets[facetName];
        }
      }

      return updatedFacets;
    });

    // Reset to page 1 when filters change
    setCurrentPage(1);
  };

  // Clear all filters
  const clearAllFilters = () => {
    setSelectedFacets({});
    setCurrentPage(1);
  };

  // Handle sort change
  const handleSortChange = (event) => {
    setSortBy(event.target.value);
    setCurrentPage(1);
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
      handleSearch(newPage);

      // Scroll to top of results
      const resultsContainer = document.querySelector('.results-container');
      if (resultsContainer) {
        resultsContainer.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  // Toggle sidebar on mobile
  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  // Handle item selection
  const handleItemSelect = (item) => {
    setSelectedItem(item);
    setShowDetailPanel(true);

    // Close sidebar on mobile when an item is selected
    if (window.innerWidth <= 768) {
      setSidebarOpen(false);
    }
  };

  // Close detail panel
  const closeDetailPanel = () => {
    setShowDetailPanel(false);
    setSelectedItem(null);

    // Return focus to the selected item card when panel closes
    setTimeout(() => {
      const selectedCard = document.querySelector('.result-item.selected');
      if (selectedCard) {
        selectedCard.focus();
      }
    }, 10);
  };

  // Toggle download options
  const toggleDownloadOptions = () => {
    setShowDownloadOptions(prev => !prev);
  };

  // Handle download
  const handleDownload = (format) => {
    // Close dropdown
    setShowDownloadOptions(false);

    // Implement download functionality based on format
    alert(`Download as ${format} would happen here`);
  };

  // Handle click outside sidebar to close it on mobile
  const handleClickOutside = (e) => {
    if (sidebarOpen && window.innerWidth <= 768) {
      const sidebar = document.querySelector('.sidebar');
      const toggleButton = document.querySelector('.sidebar-toggle');

      if (sidebar && !sidebar.contains(e.target) && toggleButton && !toggleButton.contains(e.target)) {
        setSidebarOpen(false);
      }
    }
  };

  // Add event listener for clicks outside sidebar
  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [sidebarOpen]);

  // Lock body scroll when detail panel is open
  useEffect(() => {
    if (showDetailPanel) {
      // Prevent scrolling on the body when detail panel is open
      document.body.style.overflow = 'hidden';
    } else {
      // Restore scrolling when detail panel is closed
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [showDetailPanel]);

  // Handle query type change
  const handleQueryTypeChange = (type) => {
    setQueryType(type);
    // Perform a new search with the selected query type
    if (searchPerformed) {
      handleSearch(1);
    }
  };

  const totalPages = Math.ceil(totalCount / pageSize);

  return (
    <div className="app">
      {/* Skip to content link for accessibility */}
      <a href="#main-content" className="skip-link">Skip to content</a>

      <header className="app-header" role="banner">
        <div className="header-content">
          <h1>Medicare Benefits Schedule Search</h1>
          <p>Search for MBS items, fees, and descriptions</p>
        </div>
      </header>

      <main className="app-content" id="main-content" role="main">
        <div className="search-container">
          <SearchBar 
            onSearch={handleQuerySubmit} 
            loading={loading} 
            searchTerm={query} 
            setSearchTerm={handleQueryChange}
            spellingSuggestions={spellingSuggestions}
          />

          {error && (
            <div className="error-message" role="alert" aria-live="assertive">
              <p>{error}</p>
            </div>
          )}
        </div>

        <div className="results-container">
          <aside 
            className={`sidebar ${sidebarOpen ? 'open' : ''}`} 
            aria-label="Filters" 
            aria-expanded={sidebarOpen}
          >
            <div className="filters-section">
              <h2 id="filters-heading">Filters</h2>
              {Object.keys(selectedFacets).length > 0 && (
                <button 
                  onClick={clearAllFilters}
                  className="clear-filters-button"
                  aria-label="Clear all filters"
                >
                  Clear all filters
                </button>
              )}

              <FacetFilters 
                facets={facets} 
                selectedFacets={selectedFacets} 
                onFacetChange={handleFacetChange}
                formatFacetName={formatFacetName}
              />
            </div>
          </aside>

          <button 
            className="sidebar-toggle" 
            onClick={toggleSidebar}
            aria-expanded={sidebarOpen}
            aria-controls="filters-section"
            aria-label={sidebarOpen ? "Close filters" : "Open filters"}
          >
            <span className="toggle-icon"></span>
            <span className="toggle-text">Filters</span>
          </button>

          <div className="results-section">
            <div className="results-header">
              <div className="results-count">
                {searchPerformed && (
                  <p aria-live="polite">
                    {loading ? 'Searching...' : `${totalCount.toLocaleString()} results found`}
                  </p>
                )}
              </div>

              <div className="search-options">
                <div className="sort-options">
                  <label htmlFor="sort-select">Sort by:</label>
                  <select 
                    id="sort-select" 
                    value={sortBy} 
                    onChange={handleSortChange}
                    aria-label="Sort results by"
                  >
                    <option value="relevance">Relevance</option>
                    <option value="item_num_asc">Item Number (Low to High)</option>
                    <option value="item_num_desc">Item Number (High to Low)</option>
                    <option value="fee_asc">Fee (Low to High)</option>
                    <option value="fee_desc">Fee (High to Low)</option>
                  </select>
                </div>

                <div className="query-type-options">
                  <label htmlFor="query-type-select">Search mode:</label>
                  <select 
                    id="query-type-select" 
                    value={queryType} 
                    onChange={(e) => handleQueryTypeChange(e.target.value)}
                    aria-label="Search mode"
                  >
                    <option value="hybrid">Hybrid (Recommended)</option>
                    <option value="simple">Simple</option>
                    <option value="semantic">Semantic</option>
                    <option value="fuzzy">Fuzzy</option>
                  </select>
                </div>

                <div className="download-options">
                  <button 
                    className="download-button" 
                    onClick={toggleDownloadOptions}
                    aria-expanded={showDownloadOptions}
                    aria-haspopup="true"
                    disabled={results.length === 0}
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                      <path d="M21 15V19C21 19.5304 20.7893 20.0391 20.4142 20.4142C20.0391 20.7893 19.5304 21 19 21H5C4.46957 21 3.96086 20.7893 3.58579 20.4142C3.21071 20.0391 3 19.5304 3 19V15" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M7 10L12 15L17 10" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 15V3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                    <span>Download</span>
                  </button>

                  {showDownloadOptions && (
                    <div className="download-dropdown">
                      <button onClick={() => handleDownload('csv')}>CSV</button>
                      <button onClick={() => handleDownload('excel')}>Excel</button>
                      <button onClick={() => handleDownload('pdf')}>PDF</button>
                    </div>
                  )}
                </div>
              </div>
            </div>

            <SearchResults 
              results={results} 
              loading={loading} 
              onItemSelect={handleItemSelect}
              selectedItem={selectedItem}
            />

            {totalCount > pageSize && (
              <Pagination 
                currentPage={currentPage} 
                totalPages={totalPages} 
                onPageChange={handlePageChange}
              />
            )}
          </div>
        </div>

        {/* Detail Panel */}
        {showDetailPanel && selectedItem && (
          <DetailPanel 
            item={selectedItem} 
            onClose={closeDetailPanel}
          />
        )}
      </main>

      <footer className="app-footer" role="contentinfo">
        <div className="footer-content">
          <p>&copy; {new Date().getFullYear()} Department of Health and Aged Care. All rights reserved.</p>
          <div className="footer-links">
            <a href="#privacy" onClick={(e) => e.preventDefault()}>Privacy Policy</a>
            <a href="#terms" onClick={(e) => e.preventDefault()}>Terms of Use</a>
            <a href="#accessibility" onClick={(e) => e.preventDefault()}>Accessibility</a>
          </div>
        </div>
      </footer>

      {/* Development Test Components */}
      {process.env.NODE_ENV === 'development' && (
        <div className="dev-test-components">
          <details>
            <summary>Development Test Components</summary>
            <div className="test-components-container">
              <ApiTest />
              <SearchHighlightTest />
              <SemanticSearchTest />
            </div>
          </details>
        </div>
      )}
    </div>
  );
}

export default App;
