// File structure for our React Azure Search application

// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import { SearchBar } from './components/SearchBar';
import { SearchResults } from './components/SearchResults';
import { FacetFilters } from './components/FacetFilters';
import { DetailPanel } from './components/DetailPanel';
import { searchMBS } from './services/searchService';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [facets, setFacets] = useState({});
  const [selectedFacets, setSelectedFacets] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [totalCount, setTotalCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('relevance');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const pageSize = 10;

  useEffect(() => {
    if (query.trim() || Object.keys(selectedFacets).length > 0) {
      handleSearch();
    }
  }, [currentPage, sortBy, selectedFacets]);

  const handleSearch = async () => {
    if (!query.trim() && Object.keys(selectedFacets).length === 0) {
      setResults([]);
      setFacets({});
      setTotalCount(0);
      return;
    }

    setIsLoading(true);
    try {
      const response = await searchMBS(
        query, 
        selectedFacets, 
        currentPage, 
        pageSize, 
        sortBy
      );

      setResults(response.results);
      setFacets(response.facets);
      setTotalCount(response.count);

      // Clear selected item if it's not in the new results
      if (selectedItem && !response.results.find(item => item.id === selectedItem.id)) {
        setSelectedItem(null);
      }
    } catch (error) {
      console.error('Search error:', error);
      // Handle error state here
    } finally {
      setIsLoading(false);
    }
  };

  const handleQueryChange = (newQuery) => {
    setQuery(newQuery);
  };

  const handleQuerySubmit = () => {
    setCurrentPage(1);
    handleSearch();
  };

  const handleItemSelect = (item) => {
    setSelectedItem(item);
    // On mobile, close sidebar when an item is selected
    if (window.innerWidth <= 768) {
      setSidebarOpen(false);
    }
  };

  const handleFacetChange = (facetName, value, isChecked) => {
    setSelectedFacets(prev => {
      const newFacets = { ...prev };

      if (!newFacets[facetName]) {
        newFacets[facetName] = [];
      }

      if (isChecked) {
        newFacets[facetName] = [...newFacets[facetName], value];
      } else {
        newFacets[facetName] = newFacets[facetName].filter(v => v !== value);
        if (newFacets[facetName].length === 0) {
          delete newFacets[facetName];
        }
      }

      return newFacets;
    });

    setCurrentPage(1);
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const clearAllFilters = () => {
    setSelectedFacets({});
    setCurrentPage(1);
  };

  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  const totalPages = Math.ceil(totalCount / pageSize);

  return (
    <div className="app">
      <header className="app-header">
        <div className="logo">
          <h1>New MBS Search</h1>
        </div>
        <SearchBar 
          query={query} 
          onQueryChange={handleQueryChange} 
          onQuerySubmit={handleQuerySubmit}
          isLoading={isLoading} 
        />
        <button className="mobile-menu-btn" onClick={toggleSidebar}>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <line x1="3" y1="12" x2="21" y2="12"></line>
            <line x1="3" y1="6" x2="21" y2="6"></line>
            <line x1="3" y1="18" x2="21" y2="18"></line>
          </svg>
        </button>
      </header>

      <div className="main-content">
        <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <div className="filters-container">
            {Object.keys(selectedFacets).length > 0 && (
              <div className="clear-filters">
                <button onClick={clearAllFilters}>Clear Filter(s)</button>
              </div>
            )}
            <FacetFilters 
              facets={facets} 
              selectedFacets={selectedFacets} 
              onFacetChange={handleFacetChange} 
            />
          </div>
        </aside>

        <main className="content">
          <div className="results-header">
            {totalCount > 0 && !isLoading && (
              <div className="results-count">
                <span>{(currentPage - 1) * pageSize + 1} - {Math.min(currentPage * pageSize, totalCount)} of {totalCount}</span>
              </div>
            )}

            {totalCount > 0 && (
              <div className="pagination">
                <button 
                  onClick={() => handlePageChange(1)} 
                  disabled={currentPage === 1}
                  className="pagination-first"
                >
                  «
                </button>
                <button 
                  onClick={() => handlePageChange(currentPage - 1)} 
                  disabled={currentPage === 1}
                  className="pagination-prev"
                >
                  ‹
                </button>
                <span className="current-page">{currentPage}</span>
                <button 
                  onClick={() => handlePageChange(currentPage + 1)} 
                  disabled={currentPage === totalPages}
                  className="pagination-next"
                >
                  ›
                </button>
                <button 
                  onClick={() => handlePageChange(totalPages)} 
                  disabled={currentPage === totalPages}
                  className="pagination-last"
                >
                  »
                </button>
                <span className="page-count">/ {totalPages}</span>
              </div>
            )}
          </div>

          <SearchResults 
            results={results} 
            isLoading={isLoading} 
            selectedItem={selectedItem}
            onItemSelect={handleItemSelect} 
          />

          {selectedItem && (
            <DetailPanel item={selectedItem} />
          )}
        </main>
      </div>

      <footer className="app-footer">
        <p> 2025 Medicare Benefits Schedule Search</p>
      </footer>
    </div>
  );
}

export default App;
