// File structure for our React Azure Search application

// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import { SearchBar } from './components/SearchBar';
import { SearchResults } from './components/SearchResults';
import { FacetFilters } from './components/FacetFilters';
import { DetailPanel } from './components/DetailPanel';
import { searchMBS } from './services/searchService';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [facets, setFacets] = useState({});
  const [selectedFacets, setSelectedFacets] = useState({});
  const [isLoading, setIsLoading] = useState(false);
  const [totalCount, setTotalCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('relevance');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const pageSize = 10;

  useEffect(() => {
    if (query.trim() || Object.keys(selectedFacets).length > 0) {
      handleSearch();
    }
  }, [currentPage, sortBy, selectedFacets]);

  const handleSearch = async () => {
    if (!query.trim() && Object.keys(selectedFacets).length === 0) {
      setResults([]);
      setFacets({});
      setTotalCount(0);
      return;
    }

    setIsLoading(true);
    try {
      const response = await searchMBS(
        query, 
        selectedFacets, 
        currentPage, 
        pageSize, 
        sortBy
      );

      setResults(response.results);
      setFacets(response.facets);
      setTotalCount(response.count);

      // Clear selected item if it's not in the new results
      if (selectedItem && !response.results.find(item => item.id === selectedItem.id)) {
        setSelectedItem(null);
      }
    } catch (error) {
      console.error('Search error:', error);
      // Handle error state here
    } finally {
      setIsLoading(false);
    }
  };

  const handleQueryChange = (newQuery) => {
    setQuery(newQuery);
  };

  const handleQuerySubmit = () => {
    setCurrentPage(1);
    handleSearch();
  };

  const handleItemSelect = (item) => {
    setSelectedItem(item);
    // On mobile, close sidebar when an item is selected
    if (window.innerWidth <= 768) {
      setSidebarOpen(false);
    }
  };

  const handleFacetChange = (facetName, values) => {
    setSelectedFacets(prev => {
      const newFacets = { ...prev };

      if (values.length === 0) {
        delete newFacets[facetName];
      } else {
        newFacets[facetName] = values;
      }

      return newFacets;
    });

    setCurrentPage(1);
  };

  const handleSortChange = (e) => {
    setSortBy(e.target.value);
    setCurrentPage(1);
  };

  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
  };

  const clearAllFilters = () => {
    setSelectedFacets({});
    setCurrentPage(1);
  };

  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  const totalPages = Math.ceil(totalCount / pageSize);

  return (
    <div className="app">
      <header className="app-header">
        <div className="logo">
          <h1>MBS Search</h1>
        </div>
        <SearchBar 
          query={query} 
          onQueryChange={handleQueryChange} 
          onQuerySubmit={handleQuerySubmit}
          isLoading={isLoading} 
        />
        <button className="mobile-menu-btn" onClick={toggleSidebar}>
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <line x1="3" y1="12" x2="21" y2="12"></line>
            <line x1="3" y1="6" x2="21" y2="6"></line>
            <line x1="3" y1="18" x2="21" y2="18"></line>
          </svg>
        </button>
      </header>

      <div className="main-content">
        <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <div className="filters-header">
            <h2>Filters</h2>
            {Object.keys(selectedFacets).length > 0 && (
              <button className="clear-filters-btn" onClick={clearAllFilters}>
                Clear All
              </button>
            )}
          </div>
          <FacetFilters 
            facets={facets} 
            selectedFacets={selectedFacets} 
            onFacetChange={handleFacetChange} 
          />
        </aside>

        <main className="content">
          <div className="results-header">
            <div className="results-count">
              {totalCount > 0 && !isLoading ? (
                <span>Showing {Math.min((currentPage - 1) * pageSize + 1, totalCount)} - {Math.min(currentPage * pageSize, totalCount)} of {totalCount} results</span>
              ) : null}
            </div>

            {results.length > 0 && (
              <div className="sort-options">
                <label htmlFor="sort-select">Sort by:</label>
                <select 
                  id="sort-select" 
                  value={sortBy} 
                  onChange={handleSortChange}
                >
                  <option value="relevance">Relevance</option>
                  <option value="itemNumber">Item Number</option>
                  <option value="fee">Fee (High to Low)</option>
                  <option value="fee-asc">Fee (Low to High)</option>
                </select>
              </div>
            )}
          </div>

          <div className="results-container">
            <div className="results-panel">
              <SearchResults 
                results={results} 
                isLoading={isLoading} 
                selectedItem={selectedItem}
                onItemSelect={handleItemSelect} 
              />

              {totalPages > 1 && !isLoading && (
                <div className="pagination">
                  <button 
                    onClick={() => handlePageChange(currentPage - 1)} 
                    disabled={currentPage === 1}
                  >
                    Previous
                  </button>
                  <span className="page-info">
                    Page {currentPage} of {totalPages}
                  </span>
                  <button 
                    onClick={() => handlePageChange(currentPage + 1)} 
                    disabled={currentPage === totalPages}
                  >
                    Next
                  </button>
                </div>
              )}
            </div>

            {selectedItem && (
              <div className="detail-container">
                <DetailPanel item={selectedItem} />
              </div>
            )}
          </div>
        </main>
      </div>

      <footer className="app-footer">
        <p> 2025 Medicare Benefits Schedule Search</p>
      </footer>
    </div>
  );
}

export default App;
