import React, { useState } from 'react';
import './SearchBar.css';

export const SearchBar = ({ query, onQueryChange, onQuerySubmit, loading }) => {
  const [inputValue, setInputValue] = useState(query);

  const handleSubmit = (e) => {
    e.preventDefault();
    onQuerySubmit();
  };

  return (
    <div className="search-container">
      <h2>Search MBS Items</h2>
      <p className="search-description">
        Enter item number, description, or keywords to find Medicare Benefits Schedule items
      </p>

      <form className="search-bar" onSubmit={handleSubmit}>
        <div className="search-input-wrapper">
          <input
            type="text"
            value={inputValue}
            onChange={(e) => {
              setInputValue(e.target.value);
              onQueryChange(e.target.value);
            }}
            placeholder="Search by item number, description or keywords..."
            className="search-input"
            aria-label="Search MBS items"
          />
          <button type="submit" className="search-button" disabled={loading}>
            {loading ? (
              <div className="loading-spinner" aria-hidden="true"></div>
            ) : (
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M21 21L15 15M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10Z" 
                  stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            )}
            <span className="sr-only">Search</span>
          </button>
        </div>
      </form>
    </div>
  );
};
