import React from 'react';
import './SearchBar.css';

export const SearchBar = ({ searchTerm, setSearchTerm, onSearch, loading }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch();
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      onSearch();
    }
  };

  return (
    <div className="search-bar-container">
      <div className="search-bar-header">
        <h2>Search any words with filters here.</h2>
      </div>
      <div className="search-bar">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Search MBS..."
          aria-label="Search Medicare Benefits Schedule"
          className="search-input"
        />
        <button 
          onClick={handleSubmit}
          className="search-button"
          aria-label="Search"
          disabled={loading}
        >
          {loading ? (
            <span className="loading-spinner"></span>
          ) : (
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M21 21L15 15M17 10C17 13.866 13.866 17 10 17C6.13401 17 3 13.866 3 10C3 6.13401 6.13401 3 10 3C13.866 3 17 6.13401 17 10Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          )}
        </button>
      </div>
      <div className="search-help">
        <a href="#" onClick={(e) => { e.preventDefault(); alert('Search help would go here'); }}>
          Click here for MBS Search Help.
        </a>
      </div>
    </div>
  );
};
