import React, { useState, useEffect } from 'react';
import { SearchBar } from './components/SearchBar';
import { SearchResults } from './components/SearchResults';
import { FacetFilters } from './components/FacetFilters';
import { DetailPanel } from './components/DetailPanel';
import { Pagination } from './components/Pagination';
import { searchItems } from './services/searchService';
import './App.css';

function App() {
  // State
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [facets, setFacets] = useState({});
  const [selectedFacets, setSelectedFacets] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedItem, setSelectedItem] = useState(null);
  const [showDetailPanel, setShowDetailPanel] = useState(false);
  const [totalCount, setTotalCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('relevance');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const pageSize = 10;

  useEffect(() => {
    // Initial search if needed
    if (query) {
      handleSearch();
    }
  }, [selectedFacets, sortBy]);

  // Handle search
  const handleSearch = async (page = currentPage) => {
    if (!query.trim()) {
      setResults([]);
      setFacets({});
      setTotalCount(0);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await searchItems(query, selectedFacets, page, sortBy);

      setResults(response.results);
      setFacets(response.facets);
      setTotalCount(response.count);
      setCurrentPage(page);
    } catch (err) {
      setError('An error occurred while searching. Please try again.');
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  // Handle query change
  const handleQueryChange = (newQuery) => {
    setQuery(newQuery);
  };

  // Handle query submit
  const handleQuerySubmit = () => {
    setCurrentPage(1);
    handleSearch(1);
  };

  // Handle facet selection
  const handleFacetChange = (facetName, facetValue, isChecked) => {
    setSelectedFacets(prevFacets => {
      const updatedFacets = { ...prevFacets };

      if (!updatedFacets[facetName]) {
        updatedFacets[facetName] = [];
      }

      if (isChecked) {
        updatedFacets[facetName] = [...updatedFacets[facetName], facetValue];
      } else {
        updatedFacets[facetName] = updatedFacets[facetName].filter(value => value !== facetValue);

        if (updatedFacets[facetName].length === 0) {
          delete updatedFacets[facetName];
        }
      }

      return updatedFacets;
    });
  };

  // Handle item selection
  const handleItemSelect = (item) => {
    setSelectedItem(item);
    setShowDetailPanel(true);
    // On mobile, close sidebar when an item is selected
    if (window.innerWidth <= 768) {
      setSidebarOpen(false);
    }
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
    handleSearch(newPage);
  };

  // Handle sort change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // Clear all filters
  const clearAllFilters = () => {
    setSelectedFacets({});
  };

  // Toggle sidebar on mobile
  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  // Close detail panel
  const handleCloseDetailPanel = () => {
    setShowDetailPanel(false);
    setSelectedItem(null);
  };

  const totalPages = Math.ceil(totalCount / pageSize);

  return (
    <div className="app">
      <header className="app-header">
        <div className="header-content">
          <h1>MBS Search</h1>
          <p>Search the Medicare Benefits Schedule</p>
        </div>
      </header>

      <div className="main-container">
        <button 
          className="sidebar-toggle" 
          onClick={toggleSidebar}
          aria-label="Toggle filters"
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M3 6H21M3 12H21M3 18H21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </button>

        <aside className={`sidebar ${sidebarOpen ? 'open' : ''}`}>
          <SearchBar 
            query={query}
            onQueryChange={handleQueryChange}
            onQuerySubmit={handleQuerySubmit}
            loading={loading} 
          />

          {Object.keys(facets).length > 0 && (
            <div className="filters-container">
              {Object.keys(selectedFacets).length > 0 && (
                <div className="clear-filters">
                  <button onClick={clearAllFilters}>Clear all filters</button>
                </div>
              )}

              <FacetFilters 
                facets={facets}
                selectedFacets={selectedFacets}
                onFacetChange={handleFacetChange}
              />
            </div>
          )}
        </aside>

        <main className="main-content">
          <div className="results-container">
            {error && <div className="error-message">{error}</div>}

            {results.length > 0 && (
              <div className="results-header">
                <div className="results-count">
                  {totalCount} results found
                </div>

                <div className="sort-control">
                  <label htmlFor="sort-select">Sort by:</label>
                  <select 
                    id="sort-select"
                    value={sortBy}
                    onChange={handleSortChange}
                  >
                    <option value="relevance">Relevance</option>
                    <option value="item_num_asc">Item Number (Asc)</option>
                    <option value="item_num_desc">Item Number (Desc)</option>
                    <option value="fee_asc">Fee (Low to High)</option>
                    <option value="fee_desc">Fee (High to Low)</option>
                  </select>
                </div>
              </div>
            )}

            <SearchResults 
              results={results}
              loading={loading}
              selectedItem={selectedItem}
              onSelectItem={handleItemSelect} 
            />

            {totalPages > 1 && (
              <Pagination 
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={handlePageChange}
              />
            )}
          </div>
        </main>

        {showDetailPanel && (
          <div className={`detail-panel-container ${showDetailPanel ? 'open' : ''}`}>
            <DetailPanel 
              item={selectedItem}
              onClose={handleCloseDetailPanel}
            />
          </div>
        )}
      </div>

      <footer className="app-footer">
        <div className="footer-content">
          <p> 2025 Medicare Benefits Schedule Search</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
