// Azure Cognitive Search service configuration
const SEARCH_API_ENDPOINT = process.env.REACT_APP_AZURE_SEARCH_SERVICE || 'https://testopenaiservice102.search.windows.net';
const SEARCH_API_KEY = process.env.REACT_APP_AZURE_SEARCH_API_KEY || 'cEbz7StL7nXVJ2wx6VNO9fXzw1m9hbhtdgTwkBFY90AzSeAQGwJ2'; 
const SEARCH_INDEX_NAME = process.env.REACT_APP_AZURE_SEARCH_INDEX_NAME || 'azuresql-index-v2';
const SEARCH_API_VERSION = '2021-04-30-Preview'; // Using preview version for semantic search

/**
 * Search for MBS items using Azure Cognitive Search
 * @param {string} query - The search query
 * @param {Object} selectedFacets - Selected facet filters
 * @param {number} page - Current page number
 * @param {string} sortBy - Sort option
 * @param {string} queryType - Query type (simple or semantic)
 * @param {number} fuzzyLevel - Fuzzy search level (0-3, where 0 means no fuzzy search)
 * @returns {Promise<Object>} - Search results, facets, and count
 */
export const searchItems = async (query, selectedFacets = {}, page = 1, sortBy = 'relevance', queryType = 'simple', fuzzyLevel = 0) => {
  try {
    console.log('Searching with query:', query);
    console.log('API Endpoint:', SEARCH_API_ENDPOINT);
    console.log('Index Name:', SEARCH_INDEX_NAME);
    console.log('Query Type:', queryType);
    console.log('Fuzzy Level:', fuzzyLevel);

    // Build the search request
    const searchRequest = {
      search: query || '*', // Use '*' for empty queries to return all results
      queryType: queryType === 'semantic' ? 'semantic' : 'simple', // Use semantic or simple query type
      searchFields: 'Description', // Search in Description field
      select: [
        'MBSItemId', 'ItemNum', 'ItemNumAlias', 'Description', 'HumanReadableDescription',
        'Category', 'CategoryDescription', 'Group', 'GroupDescription', 'ItemType',
        'ItemStartDate', 'ItemEndDate', 'ScheduleFee', 'NewItem', 'FeeType'
      ].join(','),
      count: true,
      facets: [
        'CategoryDescription',
        'GroupDescription'
      ],
      skip: (page - 1) * 10,
      top: 10,
      highlight: 'Description', // Add highlighting for Description field
      highlightPreTag: '<mark>', // HTML tag to wrap highlighted terms (start)
      highlightPostTag: '</mark>' // HTML tag to wrap highlighted terms (end)
    };

    // Add fuzzy search if enabled
    if (fuzzyLevel > 0 && fuzzyLevel <= 3) {
      searchRequest.searchMode = 'all'; // Match all terms (AND operator)
      searchRequest.queryType = 'full'; // Use full Lucene query syntax for fuzzy search

      // Reformat query for fuzzy search if it's not empty
      if (query && query.trim() !== '') {
        // Split the query into words and add fuzzy operator to each
        const words = query.trim().split(/\s+/);
        const fuzzyQuery = words.map(word => `${word}~${fuzzyLevel}`).join(' ');
        searchRequest.search = fuzzyQuery;
        console.log('Fuzzy query:', fuzzyQuery);
      }
    }

    // Add semantic search configuration if queryType is semantic
    if (queryType === 'semantic') {
      searchRequest.queryLanguage = 'en-us';
      searchRequest.semanticConfiguration = 'default';
      searchRequest.queryAnswer = 'extractive|count-3';
      searchRequest.captions = 'extractive|highlight-false';
    }

    // Add filters based on selected facets
    if (Object.keys(selectedFacets).length > 0) {
      const filterExpressions = [];

      for (const [facetName, facetValues] of Object.entries(selectedFacets)) {
        if (facetValues.length > 0) {
          const facetFilters = facetValues.map(value => {
            // Handle different data types appropriately
            if (facetName === 'NewItem' || facetName === 'FeeChange' || facetName === 'ItemChange') {
              return `${facetName} eq '${value}'`;
            } else {
              return `${facetName} eq '${value.replace(/'/g, "''")}'`;
            }
          });

          filterExpressions.push(`(${facetFilters.join(' or ')})`);
        }
      }

      if (filterExpressions.length > 0) {
        searchRequest.filter = filterExpressions.join(' and ');
      }
    }

    // Add sorting based on the selected option
    if (sortBy !== 'relevance') {
      switch (sortBy) {
        case 'item_num_asc':
          searchRequest.orderby = 'ItemNum asc';
          break;
        case 'item_num_desc':
          searchRequest.orderby = 'ItemNum desc';
          break;
        case 'fee_asc':
          searchRequest.orderby = 'ScheduleFee asc';
          break;
        case 'fee_desc':
          searchRequest.orderby = 'ScheduleFee desc';
          break;
        default:
          // Default to relevance sorting
          break;
      }
    } else if (query && query.trim() !== '') {
      // For non-empty queries, sort by search score by default
      searchRequest.orderby = 'search.score() desc';
    } else {
      // For empty queries, sort by ItemNum by default
      searchRequest.orderby = 'ItemNum asc';
    }

    console.log('Search request:', JSON.stringify(searchRequest, null, 2));

    // Make the search request
    const response = await fetch(
      `${SEARCH_API_ENDPOINT}/indexes/${SEARCH_INDEX_NAME}/docs/search?api-version=${SEARCH_API_VERSION}`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'api-key': SEARCH_API_KEY
        },
        body: JSON.stringify(searchRequest)
      }
    );

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Search API error response:', errorText);
      try {
        const errorData = JSON.parse(errorText);
        throw new Error(`Search API error: ${errorData.error?.message || response.statusText}`);
      } catch (parseError) {
        throw new Error(`Search API error: ${response.status} ${response.statusText}`);
      }
    }

    const data = await response.json();
    console.log('Search API response:', data);

    // Process facets
    const facets = {};
    if (data['@search.facets']) {
      for (const [facetName, facetValues] of Object.entries(data['@search.facets'])) {
        if (facetName === 'CategoryDescription' || facetName === 'GroupDescription') {
          facets[facetName] = facetValues.map(facet => ({
            value: facet.value,
            count: facet.count,
            selected: selectedFacets[facetName]?.includes(facet.value) || false
          }));
        }
      }
    }

    // Process results and add highlighted text if available
    const processedResults = data.value.map(item => {
      // Debug highlight information
      console.log(`Item ${item.MBSItemId} highlights:`, item['@search.highlights']);

      // Check if this item has highlights in the response
      if (item['@search.highlights'] && item['@search.highlights'].Description && item['@search.highlights'].Description.length > 0) {
        return {
          ...item,
          highlightedDescription: item['@search.highlights'].Description[0]
        };
      }
      return item;
    });

    // Process semantic answers if available
    let semanticAnswers = null;
    if (data['@search.answers']) {
      semanticAnswers = data['@search.answers'].map(answer => ({
        text: answer.text,
        highlights: answer.highlights,
        score: answer.score
      }));
      console.log('Semantic answers:', semanticAnswers);
    }

    // Process semantic captions if available
    if (data['@search.captions']) {
      processedResults.forEach((item, index) => {
        if (data['@search.captions'][index]) {
          item.semanticCaption = data['@search.captions'][index].text;
        }
      });
    }

    return {
      results: processedResults || [],
      facets: facets,
      count: data['@odata.count'] || 0,
      semanticAnswers: semanticAnswers
    };
  } catch (error) {
    console.error('Error searching MBS items:', error);
    throw error;
  }
};
