import React, { useState } from 'react';
import './SearchBar.css';

export const SearchBar = ({ query, onQueryChange, onQuerySubmit, isLoading }) => {
  const [inputValue, setInputValue] = useState(query);

  const handleSubmit = (e) => {
    e.preventDefault();
    onQuerySubmit();
  };

  return (
    <div className="search-bar">
      <form className="search-form" onSubmit={handleSubmit}>
        <div className="search-icon">
          <svg xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
            <circle cx="11" cy="11" r="8"></circle>
            <line x1="21" y1="21" x2="16.65" y2="16.65"></line>
          </svg>
        </div>
        <input
          type="text"
          value={inputValue}
          onChange={(e) => {
            setInputValue(e.target.value);
            onQueryChange(e.target.value);
          }}
          placeholder="Search MBS items..."
          className="search-input"
          disabled={isLoading}
        />
        <button 
          type="submit" 
          className="search-button"
          disabled={isLoading || !inputValue.trim()}
        >
          {isLoading ? (
            <svg className="search-spinner" xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <circle cx="12" cy="12" r="10"></circle>
              <path d="M12 6v6l4 2"></path>
            </svg>
          ) : (
            'Search'
          )}
        </button>
      </form>
    </div>
  );
};
