import React, { useState, useEffect } from 'react';
import { SearchBar } from './components/SearchBar';
import { SearchResults } from './components/SearchResults';
import { FacetFilters } from './components/FacetFilters';
import { Pagination } from './components/Pagination';
import ApiTest from './components/ApiTest';
import { searchItems } from './services/searchService';
import './App.css';

function App() {
  // State
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [facets, setFacets] = useState({});
  const [selectedFacets, setSelectedFacets] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [totalCount, setTotalCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(1);
  const [sortBy, setSortBy] = useState('relevance');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [showDownloadOptions, setShowDownloadOptions] = useState(false);
  const [searchPerformed, setSearchPerformed] = useState(false);
  const pageSize = 10;

  // Load default results when the page loads
  useEffect(() => {
    handleSearch();
  }, []);

  useEffect(() => {
    // Search when facets or sort options change
    if (searchPerformed) {
      handleSearch();
    }
  }, [selectedFacets, sortBy]);

  // Add event listener for ESC key to close sidebar on mobile
  useEffect(() => {
    const handleEscKey = (event) => {
      if (event.key === 'Escape') {
        setSidebarOpen(false);
      }
    };

    window.addEventListener('keydown', handleEscKey);
    return () => {
      window.removeEventListener('keydown', handleEscKey);
    };
  }, []);

  // Handle search
  const handleSearch = async (page = currentPage) => {
    if (!query.trim()) {
      setLoading(true);
      setError(null);
      setSearchPerformed(true);

      try {
        console.log('Performing search with empty query');
        const response = await searchItems('', selectedFacets, page, sortBy);

        setResults(response.results);
        setFacets(response.facets);
        setTotalCount(response.count);
        setCurrentPage(page);

        console.log('Search results:', response.results.length);
        console.log('Total count:', response.count);
      } catch (err) {
        console.error('Search error:', err);
        setError(`An error occurred while searching: ${err.message || 'Please try again'}`);
        setResults([]);
        setFacets({});
        setTotalCount(0);
      } finally {
        setLoading(false);
      }
    } else {
      setLoading(true);
      setError(null);
      setSearchPerformed(true);

      try {
        console.log('Performing search with query:', query);
        const response = await searchItems(query, selectedFacets, page, sortBy);

        setResults(response.results);
        setFacets(response.facets);
        setTotalCount(response.count);
        setCurrentPage(page);

        console.log('Search results:', response.results.length);
        console.log('Total count:', response.count);
      } catch (err) {
        console.error('Search error:', err);
        setError(`An error occurred while searching: ${err.message || 'Please try again'}`);
        setResults([]);
        setFacets({});
        setTotalCount(0);
      } finally {
        setLoading(false);
      }
    }
  };

  // Handle query change
  const handleQueryChange = (newQuery) => {
    setQuery(newQuery);
  };

  // Handle query submit
  const handleQuerySubmit = () => {
    setCurrentPage(1);
    handleSearch(1);
  };

  // Format facet name for display
  const formatFacetName = (name) => {
    if (!name) return '';

    // Specific display names for our allowed facets
    if (name === 'CategoryDescription') return 'Category';
    if (name === 'GroupDescription') return 'Group';

    // Convert camelCase or snake_case to Title Case with spaces
    return name
      .replace(/([A-Z])/g, ' $1') // Insert space before capital letters
      .replace(/_/g, ' ') // Replace underscores with spaces
      .replace(/^\w/, c => c.toUpperCase()) // Capitalize first letter
      .trim();
  };

  // Handle facet selection
  const handleFacetChange = (facetName, facetValue, isChecked) => {
    setSelectedFacets(prevFacets => {
      const updatedFacets = { ...prevFacets };

      if (!updatedFacets[facetName]) {
        updatedFacets[facetName] = [];
      }

      if (isChecked) {
        updatedFacets[facetName] = [...updatedFacets[facetName], facetValue];
      } else {
        updatedFacets[facetName] = updatedFacets[facetName].filter(value => value !== facetValue);

        if (updatedFacets[facetName].length === 0) {
          delete updatedFacets[facetName];
        }
      }

      return updatedFacets;
    });
  };

  // Handle page change
  const handlePageChange = (newPage) => {
    setCurrentPage(newPage);
    handleSearch(newPage);
    // Scroll to top of results
    document.querySelector('.main-content')?.scrollIntoView({ behavior: 'smooth' });
  };

  // Handle sort change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  // Clear all filters
  const clearAllFilters = () => {
    setSelectedFacets({});
  };

  // Toggle sidebar on mobile
  const toggleSidebar = () => {
    setSidebarOpen(prev => !prev);
  };

  // Toggle download options
  const toggleDownloadOptions = () => {
    setShowDownloadOptions(prev => !prev);
  };

  // Handle download
  const handleDownload = (format) => {
    // Close dropdown
    setShowDownloadOptions(false);

    // Implement download functionality based on format
    alert(`Download as ${format} would happen here`);
  };

  // Handle click outside sidebar to close it on mobile
  const handleClickOutside = (e) => {
    if (sidebarOpen && window.innerWidth <= 768) {
      const sidebar = document.querySelector('.sidebar');
      const toggleButton = document.querySelector('.sidebar-toggle');

      if (sidebar && !sidebar.contains(e.target) && toggleButton && !toggleButton.contains(e.target)) {
        setSidebarOpen(false);
      }
    }
  };

  // Add event listener for clicks outside sidebar
  useEffect(() => {
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [sidebarOpen]);

  const totalPages = Math.ceil(totalCount / pageSize);

  return (
    <div className="app">
      {/* Skip to content link for accessibility */}
      <a href="#main-content" className="skip-link">Skip to content</a>

      <header className="app-header" role="banner">
        <div className="header-content">
          <h1>Medicare Benefits Schedule Search</h1>
          <p>Search for MBS items, fees, and descriptions</p>
        </div>
      </header>

      <main className="app-content" id="main-content" role="main">
        <div className="search-container">
          <SearchBar 
            onSearch={handleQuerySubmit} 
            loading={loading} 
            searchTerm={query} 
            setSearchTerm={handleQueryChange}
          />

          {error && (
            <div className="error-message" role="alert" aria-live="assertive">
              <p>{error}</p>
            </div>
          )}
        </div>

        <div className="results-container">
          <aside 
            className={`sidebar ${sidebarOpen ? 'open' : ''}`} 
            aria-label="Filters" 
            aria-expanded={sidebarOpen}
          >
            <div className="filters-section">
              <h2 id="filters-heading">Filters</h2>
              {Object.keys(selectedFacets).length > 0 && (
                <button 
                  onClick={clearAllFilters}
                  className="clear-filters-button"
                  aria-label="Clear all filters"
                >
                  Clear all filters
                </button>
              )}
              {facets && Object.keys(facets).length > 0 && (
                <div className="facets" role="group" aria-labelledby="filters-heading">
                  {/* Display Category first, then Group */}
                  {['CategoryDescription', 'GroupDescription'].map(facetName => {
                    if (!facets[facetName]) return null;

                    // Sort facet values alphabetically
                    const sortedFacetValues = [...facets[facetName]].sort((a, b) => 
                      a.value.localeCompare(b.value)
                    );

                    return (
                      <div key={facetName} className="facet-group">
                        <h3 id={`facet-${facetName}`}>{formatFacetName(facetName)}</h3>
                        <ul className="facet-list" role="group" aria-labelledby={`facet-${facetName}`}>
                          {sortedFacetValues.map(facet => (
                            <li key={`${facetName}-${facet.value}`} className="facet-item">
                              <label className="facet-label">
                                <input
                                  type="checkbox"
                                  checked={selectedFacets[facetName]?.includes(facet.value) || false}
                                  onChange={() => handleFacetChange(
                                    facetName, 
                                    facet.value, 
                                    !selectedFacets[facetName]?.includes(facet.value)
                                  )}
                                  aria-label={`${formatFacetName(facetName)}: ${facet.value}`}
                                />
                                <span className="facet-text">
                                  {facet.value}
                                </span>
                                <span className="facet-count" aria-label={`${facet.count} items`}>{facet.count}</span>
                              </label>
                            </li>
                          ))}
                        </ul>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>

            {results && results.length > 0 && (
              <div className="download-section">
                <h2 id="download-heading">Download Results</h2>
                <div className="download-options" role="group" aria-labelledby="download-heading">
                  <button 
                    className="download-button"
                    onClick={() => handleDownload('csv')}
                    aria-label="Download as CSV"
                  >
                    CSV
                  </button>
                  <button 
                    className="download-button"
                    onClick={() => handleDownload('excel')}
                    aria-label="Download as Excel"
                  >
                    Excel
                  </button>
                  <button 
                    className="download-button"
                    onClick={() => handleDownload('pdf')}
                    aria-label="Download as PDF"
                  >
                    PDF
                  </button>
                </div>
              </div>
            )}
          </aside>

          <div className="main-content">
            {loading ? (
              <div className="loading-container" role="status" aria-live="polite">
                <div className="spinner" aria-label="Loading results">
                  <div className="spinner-inner"></div>
                </div>
                <p>Loading results...</p>
              </div>
            ) : results && results.length > 0 ? (
              <>
                <div className="results-header">
                  <div className="results-count" aria-live="polite">
                    <span>{totalCount} items found</span>
                  </div>
                  <div className="results-sort">
                    <label htmlFor="sort-select">Sort by:</label>
                    <select 
                      id="sort-select" 
                      value={sortBy} 
                      onChange={handleSortChange}
                      aria-label="Sort results by"
                    >
                      <option value="relevance">Relevance</option>
                      <option value="item_num_asc">Item Number (Low to High)</option>
                      <option value="item_num_desc">Item Number (High to Low)</option>
                      <option value="fee_asc">Fee (Low to High)</option>
                      <option value="fee_desc">Fee (High to Low)</option>
                    </select>
                  </div>
                </div>

                <SearchResults 
                  results={results} 
                  loading={loading} 
                  onItemSelect={() => {}} 
                />

                {totalPages > 1 && (
                  <Pagination 
                    currentPage={currentPage} 
                    totalPages={totalPages} 
                    onPageChange={handlePageChange} 
                  />
                )}
              </>
            ) : searchPerformed ? (
              <div className="no-results" role="status">
                <p>No results found. Please try a different search term or remove some filters.</p>
              </div>
            ) : null}
          </div>
        </div>

        {/* API Test Section */}
        <div className="api-test-container">
          <ApiTest />
        </div>
      </main>

      <footer className="app-footer">
        <div className="footer-content">
          <p>&copy; {new Date().getFullYear()} Medicare Benefits Schedule Search</p>
        </div>
      </footer>

      {/* Mobile sidebar toggle */}
      <button 
        className={`sidebar-toggle ${sidebarOpen ? 'active' : ''}`}
        onClick={toggleSidebar}
        aria-expanded={sidebarOpen}
        aria-controls="sidebar"
        aria-label={sidebarOpen ? "Close filters" : "Open filters"}
      >
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
          <path d="M3 6H21M3 12H21M3 18H21" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
        </svg>
        Filters
      </button>
    </div>
  );
}

export default App;
