                  {item.highlightedDescription && (
                    <div>
                      <p dangerouslySetInnerHTML={{ __html: item.highlightedDescription }}></p>
                    </div>
                  )}
                  {item.semanticCaption && (
                    <div style={{ 
                      background: '#f8f9fa', 
                      padding: '10px', 
                      borderRadius: '4px', 
                      marginTop: '10px',
                      fontSize: '0.9em',
                      color: '#555'
                    }}>
                      <strong>AI Caption:</strong> {item.semanticCaption}
                    </div>
                  )}
                  <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                    <span style={{ 
                      background: '#e6f7ff', 
                      color: '#0070a8',
                      padding: '3px 8px',
                      borderRadius: '4px',
                      fontSize: '0.8em'
                    }}>
                      {item.CategoryDescription || 'No Category'}
                    </span>
                    <span style={{ 
                      background: '#fff7e6', 
                      color: '#d48806',
                      padding: '3px 8px',
                      borderRadius: '4px',
                      fontSize: '0.8em'
                    }}>
                      {item.ItemType || 'No Type'}
                    </span>
                  </div>
                </div>
              ))}
              {semanticResults.length > 5 && (
                <p>...and {semanticResults.length - 5} more results</p>
              )}
            </div>
          )}
        </div>

        {/* Fuzzy Search Results */}
        <div style={{ flex: '1 1 350px' }}>
          <h3>Fuzzy Search Results ({fuzzyResults.length})</h3>
          {fuzzyResults.length === 0 ? (
            <p>No results to display. Try searching for something.</p>
          ) : (
            <div>
              {fuzzyResults.slice(0, 5).map(item => (
                <div key={item.MBSItemId} style={{ 
                  marginBottom: '15px', 
                  padding: '15px', 
                  border: '1px solid #eee', 
                  borderRadius: '8px',
                  background: 'white'
                }}>
                  <h4>Item {item.ItemNum} {item['@search.score'] && <span style={{color: '#888', fontSize: '0.8em'}}>(Score: {item['@search.score'].toFixed(2)})</span>}</h4>
                  <p style={{ fontWeight: 'bold' }}>{item.Description}</p>
                  {item.highlightedDescription && (
                    <div>
                      <p dangerouslySetInnerHTML={{ __html: item.highlightedDescription }}></p>
                    </div>
                  )}
                  <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
                    <span style={{ 
                      background: '#e6f7ff', 
                      color: '#0070a8',
                      padding: '3px 8px',
                      borderRadius: '4px',
                      fontSize: '0.8em'
                    }}>
                      {item.CategoryDescription || 'No Category'}
                    </span>
                    <span style={{ 
                      background: '#fff7e6', 
                      color: '#d48806',
                      padding: '3px 8px',
                      borderRadius: '4px',
                      fontSize: '0.8em'
                    }}>
                      {item.ItemType || 'No Type'}
                    </span>
                  </div>
                </div>
              ))}
              {fuzzyResults.length > 5 && (
                <p>...and {fuzzyResults.length - 5} more results</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

