import React from 'react';
import './SearchBar.css';

export const SearchBar = ({ onSearch, loading, searchTerm, setSearchTerm }) => {
  const handleSubmit = (e) => {
    e.preventDefault();
    onSearch();
  };

  const handleChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleKeyDown = (e) => {
    // Submit on Enter key
    if (e.key === 'Enter') {
      onSearch();
    }
  };

  const handleClear = () => {
    setSearchTerm('');
    // Focus back on the search input after clearing
    document.getElementById('search-input').focus();
  };

  return (
    <div className="search-bar-container">
      <form onSubmit={handleSubmit} className="search-form" role="search">
        <div className="search-input-wrapper">
          <label htmlFor="search-input" className="visually-hidden">
            Search MBS items
          </label>
          <input
            id="search-input"
            type="search"
            className="search-input"
            placeholder="Search for MBS items, descriptions, or keywords..."
            value={searchTerm}
            onChange={handleChange}
            onKeyDown={handleKeyDown}
            aria-label="Search for Medicare Benefits Schedule items"
            autoComplete="off"
            autoFocus
          />
          {searchTerm && (
            <button 
              type="button" 
              className="clear-button" 
              onClick={handleClear}
              aria-label="Clear search"
            >
              <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          )}
        </div>
        <button 
          type="submit" 
          className="search-button" 
          disabled={loading || !searchTerm.trim()}
          aria-label="Search"
        >
          {loading ? (
            <div className="spinner" role="status" aria-label="Loading">
              <div className="spinner-inner"></div>
            </div>
          ) : (
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
              <path d="M11 19C15.4183 19 19 15.4183 19 11C19 6.58172 15.4183 3 11 3C6.58172 3 3 6.58172 3 11C3 15.4183 6.58172 19 11 19Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M21 21L16.65 16.65" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          )}
          <span className="search-button-text">Search</span>
        </button>
      </form>
      <div className="search-tips">
        <p>
          <strong>Search tips:</strong> Use specific terms like "consultation" or "procedure" for better results. 
          You can search by item number, description, or keywords.
        </p>
      </div>
    </div>
  );
};
